package com.gabrielgrs2.listrepos.coreimport android.app.Applicationimport com.gabrielgrs2.listrepos.core.di.apiModuleimport com.gabrielgrs2.listrepos.core.di.repositoryModuleimport com.gabrielgrs2.listrepos.core.di.useCaseModuleimport com.gabrielgrs2.listrepos.core.di.viewModelModuleimport kotlinx.coroutines.CoroutineScopeimport kotlinx.coroutines.Dispatchersimport kotlinx.coroutines.MainScopeimport kotlinx.coroutines.SupervisorJobimport kotlinx.coroutines.cancelimport org.koin.android.ext.koin.androidContextimport org.koin.android.ext.koin.androidLoggerimport org.koin.core.context.startKoinimport org.koin.core.logger.Levelclass ListReposApplication : Application() {    override fun onCreate() {        super.onCreate()        startKoin {            androidContext(this@ListReposApplication)            androidLogger(Level.ERROR)            modules(                apiModule,                viewModelModule,                repositoryModule,                useCaseModule            )        }    }    override fun onLowMemory() {        super.onLowMemory()        applicationScope.cancel("onLowMemory() called by system")        applicationScope = MainScope()    }    companion object {        var applicationScope = CoroutineScope(SupervisorJob() + Dispatchers.Main)    }}